    /** Quick lookup by package name */
    private val packageMap = knownDApps.associateBy { it.packageName }

    /**
     * Packages that are clearly NOT dApp Store apps and should never appear
     * in the "Available in dApp Store" section. These are system-adjacent,
     * Google services, or non-Solana apps that happen to have installer=null.
     */
    private val excludedPrefixes = listOf(
        "com.google.",
        "com.android.",
        "com.mediatek.",
        "com.samsung.",
        "com.qualcomm.",
        "com.sec.",
        "com.friendly.",
        "org.chromium.",
        "com.adappvark.toolkit"   // Our own app
    )

    /** Look up a known dApp by package name */
    fun findByPackageName(packageName: String): DAppEntry? = packageMap[packageName]

    /** Get all known package names */
    fun allPackageNames(): Set<String> = packageMap.keys

    /** Get category for a package, or "Other" if unknown */
    fun categoryFor(packageName: String): String =
        packageMap[packageName]?.category ?: "Other"

    /** Get display name for a package, or null if unknown */
    fun displayNameFor(packageName: String): String? =
        packageMap[packageName]?.displayName

    /** Check if a package should be excluded from display */
    fun isExcluded(packageName: String): Boolean =
        excludedPrefixes.any { packageName.startsWith(it) }

    /**
     * Merge dynamically discovered dApps (from device scan) with the static registry.
     * Any installed app not in the registry and not excluded gets added with its
     * real app name from the device PackageManager.
     *
     * Returns the combined set of all known + discovered DAppEntries.
     */
    fun mergedWith(discoveredApps: List<DAppEntry>): List<DAppEntry> {
        val staticPackages = packageMap.keys
        val extras = discoveredApps.filter { it.packageName !in staticPackages && !isExcluded(it.packageName) }
        return knownDApps + extras
    }
}
